import React, {Component} from 'react';

// camelCase for variables and functions, 
// PascalCase for types(classes), and 
// UPPERCASE_SNAKE_CASE for constants

// Functional componenet always returns JSX
const HelloFunctionalCompenet = () => {
  console.log('alert');
  const greeting = 'Hello Function Component!';
  // return(<div className='delete-button' onClick={() => { if (window.confirm('Are you sure you wish to delete this item?')) this.onCancel(item) } } />);
  return ( 
    <h1 onClick={ () => { alert('Alerting')}}>Hello Ols!!</h1>
  );

  return <h1>{greeting}</h1>;
}
// Class-based component
// Contain state, lifecycle methods like startUp, shutdown, receiving events
class App extends Component {
  render() {
    console.log('In App')
    return (
      <div className='App'>
        <HelloFunctionalCompenet />
      </div>
    );
  }
}

export default App;
